import React, { Component } from 'react';
import './login.css'

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: '',
      password: ''
    };
  }

  userinput = (e) => {
    this.setState({
      user: e.target.value
    })
  }

  passwordinput = e => {
    this.setState({
      password: e.target.value
    })
  }


  submit = (info) => {
    window.location.href = '/list'
    if (info.user !== '' && info.password !== '') {
      axios.post('mock/mock.js', { info }).then(response => {
        alert('1')
        /*  this.setState({
           isList: true,
           userinfo: info
         }) */

      }).catch(error => console.log(error));
    } else {
      alert('用户输入的信息不完整');
    }


  }

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-4 col-md-offset-4">
            <div className="panel panel-default">
              <div className="panel-heading">欢迎来到后台管理系统</div>
              <div className="panel-body">
                <form className="form-horizontal">
                  <div className="form-group">
                    <div className="col-md-12">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="请输入用户名"
                        value={this.state.user}
                        onChange={this.userinput}

                      />
                    </div>
                  </div>
                  <div className="form-group">
                    <div className="col-md-12">
                      <input
                        type="password"
                        className="form-control"
                        placeholder="请输入密码"
                        onChange={this.passwordinput}
                        value={this.state.password}
                      />
                    </div>
                  </div>

                  <div className="form-group">
                    <div className="col-md-offset-4 col-md-12">
                      <button className="btn btn-primary" onClick={this.submit}>登录</button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>

        </div>
      </div>



    );
  }
}

export default Login;
