import logo from './logo.svg';
import './App.css';
import Login from './components/login/Login';
import List from './components/list/List';
import { HashRouter as Router, Route, Link, Switch } from "react-router-dom";
import React, { Component } from 'react';
import axios from 'axios'



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isList: false,
      userinfo: ''
    };
  }

  submit = (info) => {
    window.location.href = '/list'
    if (info.user !== '' && info.password !== '') {
      axios.post('mock/mock.js', { info }).then(response => {
        alert('1')
        /*  this.setState({
           isList: true,
           userinfo: info
         }) */

      }).catch(error => console.log(error));
    } else {
      alert('用户输入的信息不完整');
    }


  }

  render() {
    return (
      <Router>
        <Route exact path="/" component={<Login submit={this.submit} />}></Route>
        <Route exact path="/list" component={<List user={this.state.userinfo.user} />}></Route>
      </Router>



    );
  }
}


export default App;
